import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-pokemondetail',
  imports: [CommonModule],
  templateUrl: './pokemondetail.html',
  styleUrl: './pokemondetail.css'
})
export class Pokemondetail implements OnInit {
  selectedPokemon: any = null;
  currentFormImage: string | null = null; //
  pokemons = [
    { name: 'Mew', image: '/PokemonBilder/Mew_Bild.png', audio: '/PokemonAudio/Mew_Audio.mp3', style: 'Mew-Deoxys', type: 'Psycho', pokedex: '151', color: 'rosa', category: 'Neue Art-Pokémon', size: '0,4m', weight: '4,0 Kg',info: 'Mew ist ein Mysteriöses Pokémon mit dem Typ Psycho und existiert seit der ersten Spielgeneration.<br>Im Anime taucht es zum ersten Mal in der Episode Zurück zu den Wurzeln! auf, aber auch in den Episoden Die Journalistin aus der Fremde! und Alola, Kanto! erscheint es.<br><br>Spezies:<br>Mew ist ein kleines zweibeiniges Pokémon, das sich komplett rosa präsentiert. Der relativ breite Kopf wird von zwei spitzen und ebenfalls breiten Ohren gekrönt. Das Psycho-Pokémon besitzt große Augen, die aus einer weißen Lederhaut und einer hellblauen Pupille bestehen. Die Schnauze ist abgerundet. Aus dem schlanken Körper wachsen zwei kurze Arme, die mit je drei kurzen Fingern bestückt sind. Die kräftigen Schenkel gehen in sehr lange Füße über, die sich zum Ende hin dreifach teilen. Schließlich ist Mew im Besitz eines sehr langen Schwanzes, der etwa die doppelte Länge des Körpers aufweist. Zum Ende hin wird er etwas dicker. In der schillernden Form ist Mew hellblau gefärbt.<br>Mew ist der Urvater aller Pokémon und kann somit alle Attacken per TM/VM erlernen.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Mew wird aus wissenschaftlicher Sicht als Urahn sämtlicher Pokémon betrachtet, da in seinem Genom die Gene aller bisher bekannten Pokémon-Arten zu finden sind. Dies hat auch zur Folge, dass es in der Lage ist, jede Attacken erlernen zu können. Es ist in diesem Zusammenhang jedoch vollkommen unklar, in welcher Relation Mew als potentieller Urvater aller Pokémon zu Arceus, dem Schöpfer des Pokémon-Universums, steht. In wissenschaftlichen Kreisen bezweifelten viele Experten die bloße Existenz Mews, andere Arbeitsgruppen gingen davon aus, dass Mew zwar existiert haben muss, allerdings in fernerer Vergangenheit bereits ausgestorben sei.<br>Eine Forschungsgruppe rund um Mr. Fuji ist es in der jüngeren Vergangenheit gelungen, die Existenz von Mew nachzuweisen und gaben ihm die Spezies Neue Art. Jedoch ist die Quellenlage rund um Mews Wiederentdeckung sehr uneinheitlich: Im Anime wird von einem Forscherteam, finanziert von Giovanni, in der Ruine eines Schreins tief im Amazonas-Dschungel eine antike Steinplatte gefunden, auf der ein Mew abgebildet ist. Bei genauerer Untersuchung stellt sich heraus, dass in diese Steinplatte die Augenbraue eines echten Mews eingearbeitet wurde, aus der dann im Forschungsinstitut auf New Island die DNA Mews isoliert und entschlüsselt wird. Das Forscherteam rund um ihren Leiter Dr. Fuji entschließt sich, nicht wie ursprünglich geplant, Mew zu reanimieren, sondern sie wollen den genetischen Code verändern und somit das mächtigste Pokémon der Welt erschaffen. Auf Grundlage dessen erschaffen sie Mewtu künstlich im Reagenzglas.' },
    { name: 'Celebi', image: '/PokemonBilder/Celebi_Bild.png', audio: '/PokemonAudio/Celebi_Audio.mp3', style: 'Celebi', type: 'Psycho/Pflanze', pokedex: '251', color: 'grün', category: 'Zeitreise-Pokémon', size: '0,6m', weight: '5,0 Kg',info: 'Celebi ist ein Mysteriöses Pokémon mit den Typen Psycho und Pflanze und existiert seit der zweiten Spielgeneration.<br><br>Spezies:<br>Celebi ist ein kleines, zweibeiniges Pokémon, das ganz leicht grün, hellgrün, schwarz und hellblau auftritt. Unterleib, Beine und Füße sowie die Enden seiner Haare sind hellgrüner Farbe. Bis auf die Spitzen der Fühler und die Flügel, welche beide hellblau erscheinen, ist der übrige Körper leicht grün gefärbt. Ferner sind um die Augen dicke, schwarze Umrandungen. Die Erscheinung von Celebi wird durch den großen Kopf dominiert, sein Körper ist im Vergleich dazu klein. Der Kopf an sich hat eine ovale Form und läuft zu den Haaren hin spitz zu. Die großen Augen stechen besonders hervor. Sie setzen sich aus weißer Lederhaut und großen, hellblauen Pupillen zusammen. Durch den kleinen Mund wird das Antlitz vervollständigt. Schließlich wachsen auf dem Kopf zwei schmale und mittellange Fühler. An den Seiten des kleinen Torsos wachsen die eher dicken Arme, die an den Händen drei Finger aufweisen. Auf dem Rücken sind die beiden kleinen Flügel zu erkennen und an den eher kurzen Beinen befinden sich die runden Füße. In der schillernden Form erscheint das Psycho-Pflanze-Pokémon in Rosatönen. Die Pupillen sind nun dunkelgrün.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Celebi besitzt die Gabe, durch die Zeit zu reisen. Anders als Dialga kann es aber nicht den Zeitfluss manipulieren, sondern ihm und beliebig vielen Personen seiner Wahl ist lediglich die Zeitreise möglich. Somit ist es auch dazu in der Lage, an mehreren Orten gleichzeitig zu sein. Dennoch heißt es im Volksmund, dass es Krieg und Gewalt verabscheue und nur in Zeiten des Friedens auftauche. Solange Celebi sich den Menschen zeigt, soll der Menschheit angeblich eine rosige Zukunft beschieden sein.<br>Dieses Pokémon hat generell eine starke Bindung zu Wäldern und fungiert häufig als Schutzpatron dieser. Nicht zuletzt deshalb ist es auch als Stimme des Waldes bekannt. Dabei beginnen Pflanzen wie Gräser und Bäume in seiner Nähe spontan zu wachsen. Wenn Celebi in einem Wald verschwindet, lässt es laut Legende ein Ei aus der Zukunft zurück. Dabei ist jedoch unklar, ob sich in diesem Ei ein weiteres Celebi oder ein anderes Pokémon befindet.<br>In Johto ist ihm zu Ehren als der Schutzpatron des Steineichenwaldes der Steineichenwald-Schrein errichtet worden. In Orre wurde Celebi ein eigenes Sanktuarium in einem kleinen Wäldchen hinter Emeritae gebaut. Dieser Steinaltar wurde genau an der Stelle errichtet, wo Celebi damals bei seinem ersten Erscheinen den Waldboden berührte. Es heißt, dass es wieder erscheinen würde, wenn jemand vor diesem Altar eine Zeitflöte spielt. Ferner soll sich alleine schon die reine Präsenz Celebis positiv auf die Erlösung von Crypto-Pokémon auswirken: Trifft ein Crypto-Pokémon auf Celebi, erinnert sich dieses an seinen Urzustand und öffnet die verschlossene Tür zu seinem Herzen. Auch das Sanktuarium von Emeritae scheint eine ähnliche Kraft auszuströmen, die den Vorgang der Erlösung zum Abschluss bringen kann. Wie Celebis Kraft hier im Detail funktioniert, ist unbekannt und Gegenstand der Forschung. Als Mysteriöses Pokémon besitzt Celebi keinen festen Lebensraum, sucht aber die Nähe zu alten, sauberen Wäldern. Eine besondere Beziehung hat es zum Steineichenwald in Johto und dem Heiligen Wald in Orre, wo ihm jeweils Schreine gewidmet sind, an denen es sich Auserwählten zeigt beziehungsweise seine Präsenz manchmal zu spüren ist. Da es sich bei ihm um ein Mysteriöses Pokémon handelt, ist sein Verhalten fast gänzlich unerforscht und fast alle bekannten Daten zu ihm entstammen alten Legenden.<br>Außerdem ist Celebi in der Lage die Aura von Meschen und Pokémon zu sehen, was Celebi dabei hilft zu erkennen, ob sie in der richtigen Zeit sind. Falls das nicht der Fall ist, teleportiert es die Personen und/oder die Pokémon wieder zurück in ihre eigentliche Zeit.' },
     { name: 'Jirachi', image: '/PokemonBilder/Jirachi_Bild.png', audio: '/PokemonAudio/Jirachi_Audio.mp3', style: 'Jirachi', type: 'Stahl/Psycho', pokedex: '385', color: 'gelb', category: 'Wunsch-Pokémon', size: '0,3m', weight: '1,1 Kg',info: 'Jirachi ist ein Mysteriöses Pokémon mit den Typen Stahl und Psycho und existiert seit der dritten Spielgeneration.<br><br>Aussehen und Körperbau:<br>Jirachi erinnert an eine Sternschnuppe. Es ist klein und leicht, und tritt hauptsächlich in den Farben Gelb und Weiß auf. Besonders charakteristisch ist sein gelber Kopf, welcher einen Großteil seines Körpers ausmacht. Dieser besitzt die Form eines Sterns mit drei Zacken, von denen einer nach oben zeigt und zwei in die Seiten ragen. An der Spitze dieser Zacken hängt je ein türkisfarbenes Blatt. Die drei Blätter dienen als Notizzettel, auf welche Wünsche geschrieben werden können, die Jirachi, sobald es aufwacht, erfüllt. Es hat große, schwarze Augen, an denen blaue Streifen runterlaufen, die an Tränen erinnern. Das wahre Auge von Jirachi befindet sich jedoch auf seinem Bauch und ist meist nur als halbkreisförmige Linie zu sehen, da das Pokémon überwiegend schläft und das Auge somit geschlossen ist. Ist es jedoch geöffnet, erkennt man eine gelbe Lederhaut mit grüner Iris in der Mitte. Des Weiteren hat das Pokémon zwei kleine, jedoch breite Arme und zwei lange, gelbe, Flügel, die an die Enden einer Geschenkschleife erinnern. In seiner schillernden Form sind die Zettel an den Spitzen von Jirachis Kopf rot und der weiße Körper, sowie das Gesicht, sind rötlich nuanciert.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Jirachi ist ein Pokémon, das in den Legenden der Hoenn-Region auftaucht. Die meiste Zeit befindet sich dieses Pokémon in einem Zustand des Tiefschlafes. Während es schläft beschützt eine kristalline Hülle seinen Körper vor Angriffen; es ist aber auch in Notfällen dazu in der Lage selbst zu kämpfen, ohne dabei aufzuwachen. Nur alle tausend Jahre wacht Jirachi für genau sieben Tage regelmäßig auf. Voraussetzungen dafür sind, dass sich der Milleniumskomet in unmittelbarer Nähe zur Erde befindet und ihm ein Mensch ohne böse Absichten mit klarer Stimme etwas vorsingt. Ist es erst einmal erwacht, nutzt es die sieben Tage ausschließlich dazu, die Wünsche zu erfüllen, die ihm die Menschen auf die drei Wunschzettel seines Kopfes geschrieben haben. Das wahre Auge von Jirachi befindet sich auf seinem Bauch. Mit diesem kann es die Energie aus besagtem Milleniumskomet absorbieren und für die Wunscherfüllung nutzbar machen. Angeblich soll es aber bei der Erfüllung nichts neu erschaffen können, sondern teleportiert lediglich das gewünschte Objekt zum Wünschenden. Ferner ist nicht bekannt, ob es auch die Wünsche der Menschen erfüllt, die aufgrund der Dauer seines Schlafes vor seinem Aufwachen bereits verstorben sind. Trotz, dass Jirachi den jahrhunderte-langen Schlaf hat, bleibt Jirachi wach, solange es im Besitz eines Trainers ist. Der Grund dafür ist aus spieletechnischen Sinnen nicht näher begründet.' },
    { name: 'Deoxys', image: '/PokemonBilder/Deoxys/Deoxys_Bild.png', audio: '/PokemonAudio/Deoxys_Audio.mp3', style: 'Mew-Deoxys', type: 'Psycho', pokedex: '386', color: 'rot', category: 'DNS-Pokémon', size: '1,7m', weight: '60,8 Kg',info: 'Deoxys ist ein Mysteriöses Pokémon mit dem Typ Psycho und existiert seit der dritten Spielgeneration.<br><br>Aussehen und Körperbau:<br>Deoxys ist ein außerirdisches, zweibeiniges Pokémon, das vier unterschiedliche Formen annehmen kann. Diesen Formen ist das Aussehen in der Hinsicht gemeinsam, dass sie mit einer allgemein, dominant rötlich-orangenen Farbe, einem bläulich-grünen Gesicht und drei bläulich-grünen Punkten auf dem Rücken, die als Dreieck angeordnet sind, ausgestattet sind. In der Regel hat Deoxys einen violetten Streifen in der Mitte seines Gesichts und einen lilafarbiges, kristallenes Organ in der Mitte seiner Brust. Seine weißen, runden Augen sind von rechteckigen, schwarzen Augenhöhlen umgeben. Es wird jedoch im Anime gezeigt, dass Deoxys auch ein grünes, kristallenes Organ und grüne Gesichtsstreifen haben kann.<br><br>In seiner Normalform hat Deoxys Beine mit einem segmentierten, vertikal, blauen Streifen auf dem oberen Teil. Der Bauch ist schwarz mit vertikalen Linien, während das Meiste seiner Haut rötlich-orange ist. Die Arme sind in der Lage verschiedene Formen anzunehmen, wobei normalerweise ein Paar Arme jeweils die Form einer Doppelhelix annimmt. Ein Tentakel besitzt die typische rötlich-orange Farbe Deoxys, während der andere hellblau ist. Alternativ kann es dazu humanoide Arme, mit einer roten Ober- und blauen Unterseite und einer fünffingrigen Hand, bilden.<br><br>In seiner Angriffsform, bleiben die Beine zum Ende spitz zulaufend. Allerdings sind seine blauen Streifen nicht mehr segmentiert und führen über die gesamte Länge des Beines, sondern bilden eine große Spitze auf dem Knie. Seine zwei Paare Tentakeln sind eher peitschenartiger und mit feinen Spitzen, eine rot-orange und eine blaue. Sie gleichen denen der Normalform. Sein Kopf ist dreieckig, der zusätzlich drei spitze Erweiterungen hat.<br><br>Deoxys Körper in der Verteidigungsform ist breiter, seine Beine haben einen bläulich-grünen Fleck auf dem Knie. Es behält seine zwei Paar rote und blaue Tentakeln, aber sie sind jetzt flach und breiter. Der Kopf ist mit dem Körper verschmolzen, sodass es einer Art Helm gleicht. Die rot-orange Haut deckt nun den gesamten Körper ab, sodass nichts von seinem inneren schwarzen Körperteilen sichtbar ist. Es hat einen einzelnen blauen Streifen auf jeder Schulter.<br><br>Deoxys Initiativeform hat einen eleganten schwarzen Körper mit dünnen Streifen. Die rot-orange Haut erfasst nur den Kopf, Hüftgelenke und den Bereich um seinen Kristall. Es gibt nur noch einen Tentakel pro Körperseite, der eine rot und der andere blau. Sein Kopf zeigt jetzt nach oben und die Rückseite seines Kopfes bildet nun eine lange Spitze.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Deoxys wurde durch die Mutation eines Virus gebildet. Dieses Pokémon ist sehr intelligent und sein Gehirn scheint der Kristall in seiner Brust zu sein. Dieses Organ ist in der Lage Laserstrahlen abzufeuern. Zusätzlich kann Deoxys sich mit psychokinetischen Kräften durch die Luft bewegen. Wie im Anime gezeigt wurde, ist es in der Lage, jeden Teil seines Körpers, das es durch den Kampf verloren hat, zu regenerieren. Es hat auch die Fähigkeit, eine Aurora ohne Veränderung seiner Form zu verursachen. Diese Lichter werden in verschiedenen elektromagnetischen Wellenlängen eingesetzt, um mit anderen Deoxys zu kommunizieren. Es wird auch gezeigt, dass die Sicht von Deoxys aufgrund verschiedener elektromagnetischer Wellen eingeschränkt ist, die durch Menschen, Pokémon und elektrische Gegenstände ausgestrahlt werden. Dieses Pokémon lebt im Weltraum hauptsächlich in dem Inneren eines Asteroiden⁠ , der zwischen dem Mars⁠  und Jupiter⁠  um den sogenannten Asteroidengürtel⁠  kreist.<br>Deoxys steht in einer besonderen Fehde mit dem legendären Drachen-Pokémon Rayquaza. Dieses lebt in der äußeren Ozonschicht der Erde und fühlt sich durch das Eindringen von Deoxys in sein Habitat gestört, sodass dies zwischen beiden zu einer ausgeprägten Rivalität geführt hat. In diesen Höhen liefern sich beide erbitterte Kämpfe.',forms: [{ name: 'Normalform', image: '/PokemonBilder/Deoxys/Deoxys_Bild.png' },{ name: 'Angriffsform', image: '/PokemonBilder/Deoxys/Deoxys_Attack_Bild.png' },{ name: 'Verteidigungsform', image:'/PokemonBilder/Deoxys/Deoxys_Defense_Bild.png' },{ name: 'Initiativform', image: '/PokemonBilder/Deoxys/Deoxys_Speed_Bild.png' }] },
    { name: 'Phione', image: '/PokemonBilder/Phione_Bild.png', audio: '/PokemonAudio/Phione_Audio.mp3', style: 'Phione-Manaphy', type: 'Wasser', pokedex: '489', color: 'blau', category: 'Seeprinz-Pokémon', size: '0,4m', weight: '3,1 Kg',info: 'Phione ist ein Mysteriöses Pokémon mit dem Typ Wasser und existiert seit der vierten Spielgeneration.<br><br>Aussehen und Körperbau:<br>Phione ist ein kleines Pokémon, das stark Manaphy gleicht und fast vollständig dunkelblau auftritt. Einzig auf seinem Bauch ist ein rotes, längliches Element zu erkennen. Sein Aussehen wird von seinem Kopf dominiert, der ca. vier- bis fünfmal größer als der Rest des Körpers ist. Auf dem Kopf des Wasser-Pokémon wächst ein horizontaler, ovaler Teil, aus dem wiederum ein sehr langer, schmaler Schweif ragt, der etwas länger als der komplette Körper ist. Auf dem großen, runden Kopf sind die beiden ebenfalls großen und länglich gebauten Augen zu sehen, die sich aus dunkelblauer Lederhaut, grauen Iriden und schwarzen Pupillen zusammensetzen. Ein Punkt, der mit den Augen über einen dünnen Strich verbunden ist, mustert das Gesicht. Im unteren Bereich ist der kleine Mund zu sehen. An den Seiten des kleinen Torsos wachsen die beiden langen Arme. Schließlich befindet sich unten eine kleine, breite Spitze. In der schillernden Form wirkt Phione insgesamt etwas heller. Zucht und Entwicklung: Phione hat, wie alle Mysteriösen Pokémon, keine Vorstufe und ist auch nicht dazu in der Lage, sich weiterzuentwickeln. Dennoch schlüpft es aus einem Ei durch Zucht aus Manaphy gepaart mit Ditto.' },
    { name: 'Manaphy', image: '/PokemonBilder/Manaphy/Manaphy_Bild.png', audio: '/PokemonAudio/Manaphy_Audio.mp3', style: 'Phione-Manaphy', type: 'Wasser', pokedex: '490', color: 'blau', category: 'Seeprinz-Pokémon', size: '0,3m', weight: '1,4 Kg',info: 'Manaphy ist ein Mysteriöses Pokémon mit dem Typ Wasser und existiert seit der vierten Spielgeneration.<br><br>Spezies:<br>Manaphy ist ein kleines Pokémon, das stark Phione gleicht und fast vollständig dunkelblau auftritt. Lediglich die kleinen Muster auf Gesicht und Bauch sind gelb bzw. rot gefärbt. Der große herzförmige Kopf dominiert seine Erscheinung. Auf diesem wachsen zwei längliche Strukturen, die zum Ende hin über eine rundliche Form verfügen. Das Gesicht besteht aus den großen Augen, die ihrerseits aus gelber Lederhaut, dunkelblau-schwarzen Iriden und schwarzen Pupillen bestehen. Ein Muster aus zwei Punkten befindet sich über den Augen und ist über je zwei dünne schwarze Striche mit ihnen verbunden. Der kleine Körper des Wasser-Pokémon weist als Extremitäten zwei lange Arme und zwei sehr kurze Beine auf. Manaphy kann mit Statustausch die Körper von Menschen und Pokémon vertauschen, selbst wenn es sich noch im Ei befindet. Es verfügt von Geburt an über einen ausgeprägten Heimatinstinkt, der es zurück zu seinem Geburtsort führt.<br>In der schillernden Form wirkt Manaphy insgesamt etwas heller.<br><br>Manaphy-Ei:<br>Das Ei, aus dem Manaphy schlüpft, unterscheidet sich optisch stark von den meisten anderen Pokémon-Eiern der Hauptspiele. Die Eierschale ist blau gefärbt und ähnelt einer durchsichtigen Membran. Am äußersten Rand sind acht gelb leuchtende Kugeln zu sehen und im Mittelpunkt des Eis befindet sich eine große rote Kugel, die vermutlich das Eigelb darstellt.<br><br>Zucht und Entwicklung:<br>Manaphy hat, wie alle Mysteriösen Pokémon, keine Vorstufe und ist auch nicht dazu in der Lage, sich weiterzuentwickeln. Dennoch ist Manaphy (durch Zucht mit einem Ditto) als einziges Mysteriöses Pokémon in der Lage, ein Ei zu produzieren, aus dem Phione schlüpft.',forms: [{ name: 'Manaphy', image:'/PokemonBilder/Manaphy/Manaphy_Bild.png' },{ name: 'Manaphy-Ei', image:'/PokemonBilder/Manaphy/Manaphy_Ei_Bild.png' }] },
    { name: 'Darkrai', image: '/PokemonBilder/Darkrai_Bild.png', audio: '/PokemonAudio/Darkrai_Audio.mp3', style: 'Darkrai', type: 'Unlicht', pokedex: '491', color: 'schwarz', category: 'Finsternacht-Pokémon', size: '1,5m', weight: '50,5 Kg',info: 'Darkrai ist ein Mysteriöses Pokémon mit dem Typ Unlicht und existiert seit der vierten Spielgeneration. Es bildet das Gegenstück zu Cresselia.<br><br>Aussehen und Körperbau:<br>Darkrai ist ein mittelgroßes, schwebendes Pokémon, das schwarz, weiß und rot auftritt. Der Großteil seines Körpers erscheint jedoch schwarz. Lediglich der rote Bereich um seinen Hals und der weiße obere Teil des Kopfes heben sich davon ab. Das Unlicht-Pokémon besitzt insgesamt eine geisterhafte Erscheinung. Sein Kopf ist an sich vergleichsweise klein, jedoch ist der obere Bereich, der Haaren gleicht, von respektabler Länge. Das Gesicht besteht praktisch nur aus einem sichtbaren Auge, das aus hellblauer Lederhaut sowie weißer, schmaler Pupille zusammengesetzt ist. Um den Hals befindet sich ein aus mehreren Zacken bestehender Kragen. Der Brustbereich ist länglich und dennoch relativ groß, außerdem verläuft er von vorne nach hinten. An den breiten Schultern wachsen in hintere Richtung schleierartige Gebilde und die Arme selbst sind eher schmal. Darkrai besitzt je drei Finger an seinen Händen. Das Verbindungsstück zwischen Brust und Unterleib ist äußerst schmal gebaut und der Unterleib selbst erinnert an einen kurzen Rock, wobei hinten ein länglicher Schweif zu sehen ist.<br>In der schillernden Form ist der sonst schwarze Bereich des Körpers dunkelviolett und der sonst rote hellviolett.' },
    { name: 'Arceus', image: '/PokemonBilder/Arceus_Bild.png', audio: '/PokemonAudio/Arceus_Audio.mp3', style: 'Arceus', type: 'Normal', pokedex: '493', color: 'weiß', category: 'Alpha-Pokémon', size: '3,2m', weight: '320,0 Kg',info: 'Arceus ist ein Mysteriöses Pokémon mit dem Typ Normal und existiert seit der vierten Spielgeneration. Es ist sowohl der Anführer des Seen-Trios, bestehend aus Selfe, Vesprit und Tobutz, als auch des Dimensions-Trios, bestehend aus Dialga, Palkia und Giratina.<br>Der Typ von Arceus hängt von der Tafel oder dem Z-Kristall ab, den es zum Tragen erhalten hat. Es kann dadurch jeden der 18 Typen annehmen. Trägt es weder Tafel noch Z-Kristall, ist es ein Normal-Pokémon.<br><br>Aussehen und Körperbau:<br>Arceus wahres Aussehen ist de facto unbekannt. Das nächstliegende, was man davon zuvor gesehen hat, ist ein starkes grelles Licht, welches in der Lage ist aus einer Dunkelheit zu anderen zu sprechen. Die anderen Darstellungen, die man von Arceus ansonsten sieht, sind nur Fragmente seiner selbst und zeigen nur einen Bruchteil seines Daseins. Ob das wahre Arceus genau so aussieht wie seine Fragmente, ist unbekannt.<br>Arceus ist ein großes, vierbeiniges Pokémon, das weiß, grau, gelb und grün erscheint. Der Großteil seines Körpers ist dabei weiß gefärbt. Der untere Bereich seines Kopfes, das Gesicht, der Bauch sowie der untere Teil des Schwanzes und ein kleines Areal am hinteren Bauch und am oberen Teil der Hinterbeine ist grauer Farbe. Diese genannten Bereiche weisen dünne, schwarze Streifen auf. Ein kleiner Bereich der Füße und Beine wie auch seine Stirn sind dagegen gelb. Ebenfalls gelb ist die breite Struktur um seinen Bauch, die zusätzlich über kleine grüne Punkte verfügt. Das Normal-Pokémon verfügt über einen schlanken, dennoch kräftigen Körperbau, der vor allem am Hals und den Beinen spitze Segmente beinhaltet. Sein Kopf ist von langer Form, die nach hinten geneigt ist und in diese Richtung immer schmaler wird. Die Ohren wachsen spitz nach oben und haben ihren Ursprung im unteren Teil des Gesichtes. Dieses setzt sich lediglich aus den beiden Augen und zwei grünen Punkten zusammen. Die Augen selbst bestehen aus grüner Lederhaut, roten Iriden und schwarzen, runden Pupillen. Der lange Hals verbindet den Kopf mit dem Körper. Der Torso selbst ist rundlich, jedoch schmal in der Gestalt. Ein Beinpaar wächst jeweils vorne und hinten. Zusätzlich ist ein breiter, zum Ende hin immer schmaler werdender Schwanz zu erkennen. Besonders auffällig ist die Struktur, die um den Bauch herum wächst. Es handelt sich dabei um eine ringartige Form, die den kompletten Torso umschließt und über drei Spitzen verfügt, die am Körper in Richtung Hals wachsen. Ferner sind zu den Seiten hin je zwei längere Stacheln zu sehen, die jeweils durch einen Halbkreis miteinander verbunden sind, der wiederum ein kleines Stück aus der Verbindung herauswächst.<br>Arceus Färbung ändert sich mit der jeweiligen Tafel bzw. dem jeweiligen Z-Kristall, den das Pokémon bei sich trägt.<br><br>Arceus Fähigkeit Variabilität bezieht sich auf die Ansicht einiger Religionen, ihre Gottheiten vereinigen alles Existente in sich, indem Arceus alle Typen in sich vereinigt und jeden davon annehmen kann. Die hierzu verwendeten Tafeln könnten eine Anspielung auf die Entstehung der Zehn Gebote⁠  sein, da diese Moses von Gott auf zwei Steintafeln überreicht wurden. Der Idee einer alles vereinigenden Gottheit ähnlich gibt es in einigen Religionen die Überzeugung, ihre Gottheiten seien in allem Lebendigen präsent, was sich bei Arceus in der Attacke Beerenkräfte widerspiegelt. Sie greift aber auch die Vorstellung von Nahrung als Göttergabe auf, die in polytheistischen Religionen in Form von Fruchtbarkeitsgöttern⁠ auftritt, aber z. B. auch im Christentum vorkommt (Erntedankfest⁠).<br>Die Macht, die den meisten Göttern innewohnt, zeigt sich bei Arceus bereits in den Statuswerten, denn es hat die höchste Summe der Artenspezifischen Stärken aller Pokémon (Mega-Pokémon und Proto-Pokémon ausgenommen), welche oftmals als Maß für die Stärke eines Pokémon verwendet wird. Dabei gibt es für die einzelnen Statuswerte zwar Pokémon, die über eine höhere Artenspezifische Stärke verfügen, jedoch wird Arceus so zu einem der stärksten Allrounder, da es (in Hinblick auf die Statuswerte) keine Schwächen vorweist. Arceus’ Überlegenheit zeigt sich in Attacken wie Geowurf und Hyperstrahl, da diese mit großer Stärke eines Pokémon assoziiert werden. Während die Kampf-Attacke Geowurf aufgrund seiner Art der Anwendung – der Schaden wird nicht durch Schwäche oder Resistenz beeinflusst und erhält auch keinen Typen-Bonus – und der Attackenanimation mit der Weltkugel die physische Komponente von Attacken abdeckt, deckt Hyperstrahl als eine der stärksten speziellen Attacken die andere Komponente ab. Des Weiteren zeugt Turbotempo von hoher Geschwindigkeit.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Arceus ist ein Mysteriöses Pokémon und gilt als Gott und Schöpfer der Pokémon-Welt. Besonders in der Sinnoh-Region, aber auch im Norden Johtos ist seine Legende zur Schöpfung der Welt und des Lebens auf ihr ein zentraler Teil der dortigen Mythologie.<br>Am Anfang gab es nur undurchdringbares Chaos. Mitten in diesem Chaos entwickelte sich aus dem Nichts ein Ei, aus dem Arceus schlüpfte. Das Chaos hinter sich lassend, hat es das Universum und die Welt geformt. Der Legende nach soll es dies mithilfe von tausend Armen getan haben. Was es allerdings mit diesen Armen auf sich hat, ist heute anhand seines Äußeren nicht mehr ersichtlich. Als das Universum entstand, entstanden zudem auch mystische Tafeln, die in einem engen Zusammenhang mit Arceus stehen. Theorien besagen, das Universum wurde in einer riesigen Explosion erschaffen. Und angeblich ist auch Sinnoh aus einer solchen Explosion vor langer Zeit entstanden. Der Kraterberg wurde scheinbar als Erstes in Sinnoh geschaffen.<br>Am höchsten Punkt des Kraterberges, der heute als die Speersäule bekannt ist, erschuf Arceus zwei Eier. Aus diesen Eiern schlüpften die ersten beiden Pokémon nach Arceus in diese Welt: Dialga, der Meister der Zeit und Palkia, der Meister der Dimensionen und des Raumes. Das Rad der Zeit fing an sich zu bewegen. Der Raum dehnte sich aus. Fest miteinander verwoben gaben Zeit und Raum der Welt ihre Form. Welche Rolle Mew als Urahn aller Pokémon in dieser Schöpfungsgeschichte allerdings spielt, ist völlig unklar.<br>Arceus erschuf aber noch ein drittes Ei, aus dem Giratina schlüpfte und das Dimensions-Trio komplettierte. Dieses Pokémon verkörpert die Antimaterie und soll das Gegengewicht zu Dialgas und Palkias Raum-Zeit-Gefüge darstellen. Deshalb erschuf Giratina die Zerrwelt, in der weder Zeit, noch Raum richtig fließen. Zusätzlich erschuf Arceus die drei Edelsteine Adamant-Orb, Weiß-Orb und Platinum-Orb, um die Kräfte seiner Schöpfungen zu stärken. Doch es kommt durch Giratinas Verhalten zum Bruch mit dem Schöpfer allen Lebens und es wird in seine eigene Zerrwelt verbannt. Aus diesem Grund war die Erwähnung des dritten Drachens in alten Schriften strengstens verboten. Von der Zerrwelt aus muss es über die reale Welt wachen.<br>Dialga und Palkia sprachen einen Wunsch und Materie entstand. Am Ende erschuf Arceus ein letztes Ei. Nachdem dies vollzogen war, zog sich Arceus in die von ihm erschaffene Halle des Beginns für einen immerwährenden Schlaf zurück. Legenden besagen, dass es nur mit der Azurflöte aus diesem Schlaf aufgeweckt werden könne.<br>Aus dem letzten Ei des Gottes schlüpften diesmal drei Pokémon: Selfe, Vesprit und Tobutz. Als Selfe zum ersten Mal flog, erlangten die Lebewesen die Fähigkeit Probleme zu lösen. Es war die Geburt des Wissens. Als Vesprit zum ersten Mal flog, lernten die Lebewesen Glück, Freude, Trauer und den Schmerz des Lebens kennen. Die Geburt der Gefühle. Und als Tobutz zum ersten Mal flog, erlangten Lebewesen die Entschlossenheit. Es war die Geburt der Willenskraft. Nach dem Jungfernflug tauchten sie in drei Seen tief hinab auf deren Grund. Daher werden sie auch als das Seen-Trio bezeichnet. Von dort aus sollen sie das Gleichgewicht zwischen Palkia und Dialga aufrechterhalten. Alle drei zusammen können es genau mit einem der beiden legendären Drachen aufnehmen. Können sie das Gleichgewicht zwischen den beiden Drachen nicht aufrechterhalten und kommt es zum Ungleichgewicht der Welten, greift Giratina von der Zerrwelt aus ein. Auf Giratina hingegen hat das Seen-Trio keinen Einfluss. Somit bildet das Dimensions-Trio die Materie ab, während das Seen-Trio den dazugehörigen Geist darstellt.<br>Eine bildliche Darstellung der Verhältnisse zwischen den Legendären Pokémon ist in den Höhlenmalereien der Ruinen von Elyses zu sehen. Am Eingang der Ruinen befinden sich antike Darstellungen von Dialga und Palkia. In deren Zentrum ist eine große Höhlenmalerei mit drei Wesen, die ein Dreieck bilden. Im Zentrum dieses Dreiecks befindet sich ein Lichtkegel. Die Interpretation dieser Malerei ist bei Historikern sehr umstritten. Die klassische Lesart sagt, dass das Licht in der Mitte für das Erscheinen von Dialga oder Palkia an der Speersäule steht. Die drei Wesen darum sollen vermutlich Selfe, Vesprit und Tobutz darstellen. Eine andere Lesart sieht in den drei Wesen Dialga, Palkia und Giratina. In deren Zentrum wird Arceus als Lichtkegel dargestellt. Der Zeitenlärm von Dialga und der Raumschlag von Palkia muss die Menschen, die diese Malerei angefertigt haben, bis ins Mark verängstigt haben. Diese Malerei symbolisiert die Gefühle der Ehrfurcht, des Erstaunens und alle anderen, die sie damals empfanden. Die Erinnerung daran wurde an zahlreiche Menschen weitergegeben und wurde schließlich zu einem Mythos.<br>Ein weiterer Ort, der mit der Schöpfungsgeschichte des Dimensions-Trios zusammen hängt, befindet sich weit im Norden Johtos. Vor langer Zeit verließ eine Gruppe Menschen ihr Zuhause in Sinnoh und ließ sich dort nieder. Sie machten diesen Ort zu ihrer Heimat und bauten hier einen Palast, der heute als Sinjoh-Ruinen bekannt ist. In deren Mittelpunkt befindet sich der Trysteriumaltar. Dies ist ein ehrwürdiger Altar, der zu Ehren von Arceus errichtet wurde. Der Kreis in der Mitte des Altars ist Arceus, dem Ursprung aller Dinge, gewidmet. Man sagt, die Macht von ihm wurde dort in Musik und Tanz zum Ausdruck gebracht. In Johto gibt es noch Menschen, die diese Tradition bewahren. Den alten Schriftstücken zufolge erklomm Arceus den Trysteriumaltar und es entstanden Zeit, Raum und Antimaterie, indem es weitere Eier schuf, aus denen Dialga, Palkia und Giratina schlüpften. Dies geschah auch im Zusammenhang mit den Icognito. Diese und die Alph-Ruinen in Johto scheinen ebenfalls mit Arceus in Verbindung zu stehen. Der genaue Zusammenhang ist bislang allerdings unklar.<br>Ferner wurde das Legendäre Pokémon Amigento von der Æther Foundation mit Arceus als Vorbild erschaffen. Als Grundlage hierfür nutzen sie die alten Aufzeichnungen zum Pokémon-Gott in der Fleetburg-Bibliothek.' },
    { name: 'Victini', image: '/PokemonBilder/Victini_Bild.png', audio: '/PokemonAudio/Victini_Audio.mp3', style: 'Victini', type: 'Psycho/Feuer', pokedex: '494', color: 'gelb', category: 'Sieg-Pokémon', size: '0,4m', weight: '4,0 Kg',info: 'Victini ist ein Mysteriöses Pokémon mit den Typen Psycho und Feuer und existiert seit der fünften Spielgeneration.<br><br>Aussehen und Körperbau:<br>Victini ist ein kleines, zweibeiniges Pokémon, das beige, orange und schwarz erscheint: Die Oberseite seiner Ohren, Hände und Füße sind dabei orange gefärbt, der übrige Körper beige. Das Psycho-Feuer-Pokémon hat einen im Vergleich zum Körper großen Kopf. Dies liegt vor allem an den sehr großen Ohren, die eine V-Form aufweisen. Der übrige Kopf ist rundlich und das Gesicht besteht aus den sehr großen blauen Augen, der winzigen Nase und dem Mund. Victinis Oberkörper ist recht schlank aufgebaut, wogegen der Unterkörper und vor allem die Beine sehr breit und rundlich-dick erscheinen. Die Arme sind anfangs schlank und werden zum Ende hin etwas breiter und runder, bevor sie mit den kleinen Händen enden. Die kurzen Füße haben je zwei spitze Zehen. Schließlich besitzt das Pokémon einen zweigeteilten Schwanz, der einem Flügelpaar gleicht.<br>In der schillernden Form sind die beigen Körperteile weiß und die orangenen rot gefärbt.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Victini hat die Fähigkeit Menschen Sieg und Erfolg zu schenken. Team Plasma hat sich aus diesen Gründen zum Freiheitsgarten begeben, in welchem sich Victini friedlich niedergelassen hat. Nachdem Team Plasma vertrieben werden konnte, lässt sich Victini fangen.<br><br>Im 14. Pokémon-Kinofilm lebt es nicht im Freiheitsgarten, sondern auf dem Schwert des Tieflandes, welches sich über dem Königreich des Tieflandes befindet. Diese Burg hatte Victini durch eigene Kräfte auf einen Berg getragen. Dies tat es, da der König es darum bat, nachdem sich die Bevölkerung auf der Burg versammelte, um sich vor einem enormen Krieg zu retten. Nachdem die Burg auf dem Berg landete, errichtete der König eine Absperrung, um die Burg zu schützen. Noch bevor er die Absperrung öffnen konnte, starb der Mann und Victini war auf ewig innerhalb der Absperrung gefangen. Eines Tages sollte, unter Kontrolle von Damon, Victini die Burg noch einmal versetzten. Victini weigerte sich, Damon jedoch zwang es dazu seine Kräfte an einige Monozyto weiterzugeben, wodurch diese dies tun konnten. Dank einer Vision wurde Ash, welcher dort auf der Durchreise war, bewusst, dass die Burg nicht noch einmal versetzt werden darf, da ansonsten die Drachenenergie außer Kontrolle geraten würde. Damons Plan wurde verhindert. Nach einem großen Kampf um die Burg wurde Victini befreit und war somit nicht länger innerhalb der Absperrung gefangen.' },
    { name: 'Keldeo', image: '/PokemonBilder/Keldeo/Keldeo_Bild.png', audio: '/PokemonAudio/Keldeo_Audio.mp3', style: 'Keldeo', type: 'Wasser/Kampf', pokedex: '647', color: 'gelb', category: 'Jungpferd-Pokémon', size: '1,4m', weight: '48,5 Kg',info: 'Keldeo ist ein Mysteriöses Pokémon mit den Typen Wasser und Kampf und existiert seit der fünften Spielgeneration. Zusammen mit Kobalium, Terrakium und Viridium gehört es den Rittern der Redlichkeit an.<br><br>Aussehen und Körperbau:<br>Das 1,4 Meter große und 48,5 Kilogramm schwere Wasser-Pokémon Keldeo ist ein einhornähnliches⁠  Pokémon mit einer überwiegend beigen Färbung. Der Kopf des Pokémon ist mit einem beigefarbenen Horn bestückt. Darüber lässt sich eine prachtvolle, buschige rote Mähne finden. Über den schwarzen Augen befindet sich jeweils eine lange, dunkelblaue Augenbraue. Auf der Schnauze sind zwei rot gefärbte Nasenlöcher. Vom Hals bis zur Brust hat Keldeo ein blaues Fell. In der Mitte der Brust befindet sich ein einzelner beiger Fleck. Das Pokémon besitzt vier Beine, welche mit dunkelblau gekennzeichneten Hufen bestückt sind. Am Hinterleib ist ein blauer, großer Schweif, welcher mit einem Glanzpunkt verziert ist.<br>Die Resolutform von Keldeo hat kleinere Unterschiede zur normalen Form. Das Horn ist um einiges größer und kräftiger. Außerdem ist es hier dunkelblau. Aus der ebenfalls prächtigeren Mähne stöben jetzt einige Strähnen heraus, welche grün, orange und blau sind. Die Augenbrauen bilden sich deutlich zurück und werden zudem heller. Sein Fell auf der Brust sowie sein Schweif werden etwas länger. Die Hufe bilden sich ebenfalls etwas mehr aus.<br>In der schillernden Form ist seine rote Mähne hellorange und sein blaues Fell hellgrün bis türkis gefärbt.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Keldeo ist ein Mysteriöses Pokémon, das in der Mythologie von Einall auftaucht. Es kann auf dem Wasser laufen und reist auf Flüssen und Meeren um die Welt. Es ist manchmal an malerischen Küstengebieten anzutreffen. Keldeo gleitet über das Wasser, indem es seine Hufe als Wasserdüsen nutzt. Seine Beintechnik verschafft ihm auch im Kampf Vorteile. Ist es sich seiner Sache sicher, strotzt es vor Kraft und seine Sprünge werden zu schnell für das menschliche Auge. Zudem ist Keldeo nach seiner Ausbildung bei Kobalium, Terrakium und Viridium und einem harten Training zum Meister des Schwertkampfes geworden.<br>Einst lebten Menschen und Pokémon getrennt voneinander. Zu dieser Zeit begannen die Menschen einen fürchterlichen Krieg untereinander. Ob es sich bei diesem Krieg um den Bruderkrieg zwischen den Helden und Reshiram und Zekrom handelte, ist allerdings unklar. Verursacht durch das Kriegstreiben drohte der Wald rund um das Moor von Nevaio und die darin beheimateten Pokémon zum Opfer eines großen Feuers zu werden. Die Pokémon waren gefangen inmitten all des Feuers und des Rauches. Im letzten Augenblick erschienen die Legendären Pokémon Kobalium, Terrakium und Viridium. Das Trio, im Volksmund auch bekannt als die Ritter der Redlichkeit, zogen zu diesem Zeitpunkt zusammen durch das Land, um die Armen und Schwachen zu beschützen. Angeführt von Kobalium bestraften die drei Gefährten alle Menschen, die in diesem Krieg Pokémon verletzten. Um den eingeschlossenen Pokémon zu helfen, schlug Terrakium mit seiner immensen Kraft einen Fluchtweg in den Fels. Viridium bewahrte mit seiner unglaublichen Geschwindigkeit die Pokémon vor lohendem Funkenflug. Und Kobalium wies den ängstlichen Pokémon den Weg aus dem brennenden Wald. Kobalium, Terrakium und Viridium verjagten im Anschluss auch all die Kriegstreiber mit ihrer überlegenen Kraft. Die Menschen hatten eine solche Angst vor den drei mächtigen Pokémon, dass sie die Waffen sofort niederlegten und flohen.<br>Innerhalb der Asche des nun niedergebrannten Waldes fanden sie ein einsames Keldeo. Das junge Pokémon wurde in den Kriegswirren von seinen Eltern getrennt. Kobalium, Terrakium und Viridium erkannten, dass es ohne seine Eltern nicht überleben würde und zogen das Pokémon an ihrer Stelle auf. Anstelle der Eltern lehrten sie das Junge das zum Überleben notwendige Wissen sowie die notwendigen Attacken. Im Schwurhain nördlich von Dausing sieht man auch heute noch die Stelle, an der die Ritter der Redlichkeit Keldeo den Umgang mit seinem Schwert lehrten. Dabei handelt es sich um einen riesigen Felsblock, in den die Ritter mithilfe ihrer Sanctoklingen große Kerben in das Gestein ritzten, um ihre Stärke zu demonstrieren. Nach einigen Fehlschlägen gelang es schließlich auch Keldeo, eine weitere Kerbe im Fels zu hinterlassen, wo vorher nur die drei Kerben seiner Mentoren zu sehen waren. Binnen kurzer Zeit wuchs das junge Pokémon heran und entwickelte Kräfte, welche sogar die Fähigkeiten seiner drei Pflegeeltern übertrafen. Beseelt von seiner neuen Stärke, nahm es sogar eine neue Form, die sogenannte Resolutform, an. Irgendwann verschwand Keldeo aus dem Wald, um nach seinen harten Lehrjahren und jugendlichem Tatendrang die Welt zu erkunden und niemand wusste wohin es ging.',forms: [{ name: 'Standardform', image:'/PokemonBilder/Keldeo/Keldeo_Bild.png' },{ name: 'Resolutform', image:'/PokemonBilder/Keldeo/Keldeo_Resolut_Bild.png' }] },
    { name: 'Genesect', image: '/PokemonBilder/Genesect_Bild.png', audio: '/PokemonAudio/Genesect_Audio.mp3', style: 'Genesect', type: 'Käfer/Stahl', pokedex: '649', color: 'violett', category: 'Paläozoikum-Pokémon', size: '1,5m', weight: '82,5 Kg',info: 'Genesect ist ein Mysteriöses Pokémon mit den Typen Käfer und Stahl und existiert seit der fünften Spielgeneration.<br><br>Aussehen und Körperbau:<br>Genesect ist ein aufrecht stehendes, insektenartiges Pokémon mit einem Körper aus violettem Metall. Es hat hellere violette Markierungen an den Schultern, der Kanone und am Unterleib und dessen Krallen sind ebenfalls in derselben Farbe. Es hat zwei große rote Augen, mit zwei Ringen innen, und einen mundartigen weißen Streifen dazwischen. Dieser Streifen bewegt sich sowohl im Spiel als auch im Anime nicht, daher ist es unklar, ob es sich tatsächlich um Genesects Mund handelt. Es soll aber eindeutig einen darstellen. Es hat lange dünne Arme und etwas breitere Beine, und alle diese Gliedmaße enden mit den vorher genannten Krallen.<br>Genesect ist ursprünglich ein Pokémon gewesen, welches vor 300 Millionen Jahren gelebt hat, aber es wurde von Team Plasma modifiziert. Laut manchen seiner Pokédex-Einträge ist die Kanone auf seinem Rücken von Team Plasma hinzugefügt worden, während andere Einträge behaupten sie sei lediglich verstärkt worden. Es ist daher unklar in welchem Maße Genesects ursprüngliches Äußeres modifiziert wurde. Klar ist jedoch, dass der Apparat für Module auf seinem Rücken hinzugefügt wurde.<br>Seine schillernde Form ist rot statt violett mit rosafarbenen Markierungen und Krallen und silbernen Augen.<br><br>Hintergrundgeschichte und Legendenstatus:<br>Wie bereits erwähnt war Genesect vor 300 Millionen Jahren ein mächtiger Jäger, der von vielen anderen Pokémon gefürchtet wurde. Team Plasma hat mit deren Fossilien die Genesect wiederbelebt und sie daraufhin modifiziert. Dies wurde von Team Plasma mit dem Ziel unternommen, das stärkste Pokémon überhaupt zu erschaffen. N hat das Projekt jedoch abbrechen lassen und das P2-Labor geschlossen, da er nicht wollte, dass die Pokémon durch Technologie modifiziert werden. Der Wissenschaftler Dudley arbeitete jedoch weiterhin an dem Projekt und war sauer, dass N sein Genie nicht erkannte. Er rekonstruierte Genesect und fügte die Kanone hinzu.' },
    { name: 'Diancie', image: '/PokemonBilder/Diancie/Diancie_Bild.png', audio: '/PokemonAudio/Diancie_Audio.mp3', style: 'Diancie', type: 'Gestein/Fee', pokedex: '719', color: 'rosa', category: 'Juwel-Pokémon', size: '0,7m', weight: '8,8 Kg',info: 'Diancie ist ein Mysteriöses Pokémon mit den Typen Gestein und Fee und existiert seit der sechsten Spielgeneration.<br>Seit Pokémon Omega Rubin und Alpha Saphir ist Diancie in der Lage, eine Mega-Entwicklung zu Mega-Diancie zu vollführen.<br><br>Spezies:<br><br>Diancie:<br>Diancie ist ein kleines, graues Pokémon. Sein Unterkörper besteht aus einem großen, zerklüfteten Felsbrocken, aus dem rosafarbene Edelsteine ragen. Sein Oberkörper hingegen ist in ein weißes Kleidchen gehüllt. In der schillernden Version ist Diancie dunkler gefärbt. Diancie zeichnet sich durch eine reich verzierte Kopfbedeckung aus verschiedenen rosafarbenen Kristallen aus, die ihm eine königliches Auftreten verschaffen. Der schöne Anblick seines schimmernden Körpers gilt als besonders schön.<br>Diancie gilt als Mutation von Rocara. Es lebt in großen Kolonien mit Rocara zusammen und steht diesen als Königin vor. Es hat die Fähigkeit, luftgebundene Kohlenstoffpartikel in kürzester Zeit zu Diamanten zu komprimieren, was sich in vielen seiner Attacken wie Juwelenkraft oder Tarnsteine niederschlägt. Es verfügt über die einzigartige Attacke Diamantsturm, die Schaden verursacht und zugleich seine Verteidigung steigern kann. Zudem besitzt Diancie in seiner Normalform die Fähigkeit Neutraltorso die verhindert, dass die Statuswerte von Diancie durch eine Attacke oder eine Fähigkeit des Gegners gesenkt werden.<br><br>Mega-Diancie:<br>Diancie ist in der Lage, mithilfe von Diancienit eine Mega-Entwicklung zu Mega-Diancie durchzuführen. An die Stelle des Felsbrockens, der üblicherweise seinen Unterkörper bildet, tritt ein riesiges Juwel, das von weiteren, rosafarbenen Kristallen wie ein Rock umgeben wird. Auch sein Kopfschmuck wird noch prächtiger, indem es zwei durchsichtige, bandartige Strukturen ausprägt, die es wie einen Schleier trägt.<br>In der Mega-Form besitzt es jedoch die Fähigkeit Magiespiegel, die sämtliche Status-Attacken, die auf Diancie zielen, automatisch zurück auf den Anwender lenkt.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Diancie ist die Mutation des nicht-legendären Edelstein-Pokémon Rocara und kann im Nu viele Diamanten aus luftgebundenem Kohlenstoff erzeugen, indem es den Kohlenstoff in seiner Umgebung mit seinen Händen komprimiert. Diancies rosafarbener schimmernder Körper gilt als der schönste Anblick überhaupt, was ihm unter anderem den Spitznamen Prinzessin Rosarot verschafft hat. Es lebt mit Rocara in mit Diamanten verzierten Höhlen und erstellt den wichtigen Herzdiamant.',forms: [{ name: 'Diancie', image:'/PokemonBilder/Diancie/Diancie_Bild.png' },{ name: 'Mega-Diancie', image:'/PokemonBilder/Diancie/Mega_Diancie_Bild.png' }] },
    { name: 'Hoopa', image: '/PokemonBilder/Hoopa/Hoopa_Bild.png', audio: '/PokemonAudio/Hoopa_Audio.mp3', style: 'Hoopa', type: 'Psycho/Geist', pokedex: '720', color: 'violett', category: 'Schabernack-Pokémon', size: '0,5m', weight: '9,0 Kg',info: 'Hoopa ist ein Mysteriöses Pokémon mit den Typen Psycho und Geist und existiert seit der sechsten Spielgeneration. Es besitzt neben der gebannten Form außerdem eine entfesselte Form, in der es die Typen Psycho und Unlicht besitzt.<br><br>Spezies:<br><br>Gebannte Form:<br>Hoopa ist ein eher kleines, kobold- oder geisterhaftes Pokémon. Es hat einen verhältnismäßig großen Kopf, an dessen Seiten sich zwei große, dunkle Hörner befinden. An diesen hängen goldfarbene Ringe unbekannten Materials. Hoopa bewegt sich schwebend fort und besitzt keine Beine, sein Unterkörper läuft spitz aus; um den Bauch jedoch trägt es einen weiteren goldenen Ring. Seine zwei kurzen Ärmchen sind nicht mit seinem Körper verbunden und können frei umherschweben. In seiner schillernden Form ist Hoopas Körper hellbraun und goldgelb gefärbt. Zudem haben seine Augen eine leuchtend rote Färbung.<br>Hoopa besitzt die außergewöhnliche Fähigkeit, die goldenen Ringe an seinem Körper zu beliebiger Größe auszudehnen und in ihrem Zentrum dann eine Raumkrümmung zu verursachen. Mithilfe dieser Raumportale kann es Gegenstände, die ihm gefallen, an beliebige Orte bewegen. Man sagt, dass es einst ein ganzes Schloss an einen unbekannten Ort gebracht hat, weil es auf dessen Schatz aus war. Das Schabernack-Pokémon krümmt den Raum für verschiedenste Attacken wie Wunderraum oder Bizarroraum, Trickbetrug oder Seitentausch aus. Seine Spezialattacke heißt Dimensionsloch, durch welches es sich hinter einen Gegner teleportiert, so dessen schützende Attacken wie Schutzschild durchdringt und ihn dann von hinten angreift. Auch zeigt seine Fähigkeit Zauberer auf, wie es mit seinen Kräften Items bewegt. Schließlich nutzt es seine Attacken auch für Angriffe des Psycho- oder Geist-Typs wie Psychokinese oder Spukball.<br><br>Entfesselte Form:<br>Hoopas wahre Gestalt, die entfesselte Form, hingegen ist gigantisch groß und schwer. Es hat eine nahezu menschliche Gestalt. In der Mitte seiner breiten Brust befindet sich ein schwarzes Loch, welches durch einen goldenen Ring gerahmt ist. Es hat einen brutal aussehenden Kopf mit großen Reißzähnen und zwei langen Hörnern sowie einem großen Haarschopf. Seine zwei Beine laufen in spitzen Füßen aus und auch sein großer Schweif besitzt ein gespitztes Ende. Auffällig sind seine sechs Arme, die frei um seinen Körper in der Luft gleiten.<br>Wird Hoopa mithilfe eines Banngefäßes in seine entfesselte Form versetzt, nehmen seine körperlichen Kräfte immens zu und es entwickelt dunkle Kräfte, die ihm den Unlicht-Typen verleihen. Es ist schwierig, der geballten Kraft seiner sechs Arme und den vielen Dimensionsringen zu entgehen. Bei seiner Spezialattacke Dimensionswahn schickt es seine Hände durch einzelne Dimensionsringe, welche dann aus allen Richtungen auf den Gegner einprügeln. Zudem verfügt es über Attacken wie Finsteraura oder Abschlag.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Hoopa bewohnt wüstenhafte, warme Gebiete. Der Legende zufolge wurde es einst mithilfe des Banngefäßes gebannt, da es im Kampf gegen andere Legendäre Pokémon ein Dorf zerstörte.',forms: [{ name: 'Gebanntes Hoopa', image:'/PokemonBilder/Hoopa/Hoopa_Bild.png' },{ name: 'Entfesseltes Hoppa', image:'/PokemonBilder/Hoopa/Hoopa_unbound_Bild.png' }] },
    { name: 'Volcanion', image: '/PokemonBilder/Volcanion_Bild.png', audio: '/PokemonAudio/Volcanion_Audio.mp3', style: 'Volcanion', type: 'Feuer/Wasser', pokedex: '721', color: 'braun', category: 'Dampf-Pokémon', size: '1,7m', weight: '195,0 Kg',info: 'Volcanion ist ein Mysteriöses Pokémon mit den Typen Feuer und Wasser und existiert seit der sechsten Spielgeneration.<br><br>Spezies:<br>Volcanion ist ein etwa menschenhohes, sehr schweres Pokémon, welches sich auf vier Beinen fortbewegt. Sein massiver, dunkelroter Körper läuft nach oben spitz zu und wird von den stämmigen, klauenbewehrten Beinen getragen, zudem hat es einen kurzen, aber dicken Schweif, der ebenfalls spitz zuläuft, sowie einen reptilienhaften Kopf mit einem breiten, kräftigen Kiefer. Auffallend sind seine zwei dicken Arme, welche aus seinem Rücken herausragen und die es meistens zu einem Ring geformt hat. In seiner schillernden Form hat Volcanion eine goldgelbe Farbe. Zudem sind die normalerweise orangefarbenen Zierelemente pink und die normalerweise hellblauen türkis gefärbt.<br>Volcanion, das Dampf-Pokémon, verfügt über die einzigartige Typenkombination aus Wasser und Feuer. In seinem Körper produziert es so dichten Wasserdampf, welchen es über seine Arme ausstößt. Der heiße Wasserschwall kann als seine Spezialattacke Dampfschwall Feinde verbrennen und hat zudem eine große Durchschlagskraft; auch andere mächtige Wasser-Attacken wie Siedewasser und Hydropumpe und heiße Feuerangriffe wie Flammenblitz oder Hitzekoller entfesseln. Zudem nutzt es die Kombination als Nebel aus und setzt diesen für Weißnebel oder Dunkelnebel ein. Seine Fähigkeit H2O-Absorber ermöglicht es ihm, Wasser-Attacken, die auf es gerichtet sind, zu absorbieren und sich damit zu heilen.<br>Es lebt in Bergen, die von Menschen gemieden werden, und hat eine so große Kraft, dass es damit sogar Berge versetzen können soll.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Volcanion meidet die Gesellschaft von Menschen und lebt abgeschieden in einsamen Berggebieten. Der Nebel, den es aussondert, hilft dem geheimnisvollen Pokémon dabei, sich dort zu verstecken.' },
    { name: 'Magearna', image: '/PokemonBilder/Magearna/Magearna_Bild.png', audio: '/PokemonAudio/Magearna_Audio.mp3', style: 'Magearna', type: 'Stahl/Fee', pokedex: '801', color: 'grau', category: 'Kunstwerk-Pokémon', size: '1,0m', weight: '80,5 Kg',info: 'Magearna ist ein Mysteriöses Pokémon mit den Typen Stahl und Fee und existiert seit der siebten Spielgeneration.<br><br>Spezies:<br>Magearnas Körperbau erinnert an einen humanoiden Roboter in einem vermutlich metallenen Kleid. Sein Oberkörper besteht aus einer kleinen Kugel, die mit einem an einen Pokéball erinnernden Muster verziert ist. Sein Körper ist weiß bis leicht rosafarben, in seiner schillernden Form hingegen ist es bläulich metallisch gefärbt. Magearnas Armgelenke sind ebenfalls kugelrund und sehen wie Puffärmel⁠  aus, seine Arme sind handlos und laufen spitz zu; zum Angreifen kann es diese Spitzen öffnen. Sein riesiger Rock ist ebenfalls eine Anspielung an einen Pokéball, auch lassen sich dessen Farben in Andeutungen erkennen. An der Stelle, wo der Pokéball seinen Knopf hat, befinden sich am Rock eine wappenartige Einstanzung sowie goldfarbene Verzierungen, von denen auch wie Zierbänder, welche um den Rock herum verlaufen. Um seinen runden Kopf trägt Magearna eine zahnradartige Struktur, die an die Haube einer Dienstmagd aus dem 20. Jahrhundert erinnert. Es besitzt große, rote Augen und zwei hasenohrenartige Metallplatten auf dem Kopf. Als Schillerndes Pokémon hat es einen komplett silbernen Körper, nur die Displays der Augen und des Seelenherzens bleiben unverändert.<br>Magearna wurde vor 500 Jahren geschaffen. Es stellte damals ein Geschenk für eine Königstochter dar und wurde zu diesem Zweck in bunten, rot-goldenen Farben bemalt, die als Originalfarbe bezeichnet werden. Ein schillerndes Magearna besitzt eine schwarze und weiße Originalfarbe. Dadurch erinnerte es noch mehr an einen Pokéball. Sein überreicher Schmuck machte es überaus beliebt. Im Laufe der Jahrhunderte verlor es diese Färbung jedoch. Sein Urheber war ein einzelner Wissenschaftler, der die Lebensenergie von Pokémon gesammelt und in eine künstliche Seele gegossen hat. Das eigentliche Pokémon stellt diese als "Seelenherz" bezeichnete Seele dar, während sein Körper ein bloßes mechanisches Gefäß ist, das vom Seelenherz angetrieben wird.<br>Durch das Seelenherz ist das Fabrikats-Pokémon in der Lage, die Gefühle, Gedanken und Empfindungen anderer Pokémon wahrzunehmen, indem es sie genauso durchlebt. Dies macht Magearna zu einem sehr hilfsbereiten Pokémon. Wird es dadurch traurig oder erschöpft, kann es sich zum Schlafen zusammenziehen und die Form eines Pokéballs annehmen. Man sagt, dass es die Sprache der Menschen verstehen könne.<br>Magearna hat in ihren Armen Blumensträußen, um sich erkenntlich zu zeigen. Ob die Blumen langlebig sind oder ob Magearna immer neue pflückt und als Strauß zusammenlegt, ist nicht bekannt. Die Pollen dieser Blumen bringen Volcanions zum Niesen, wodurch diese immer einen Dampfschwall ausstoßen. Außerdem hat es im Körper eine Spieluhr mit einer wohlklingenden Melodie. Wenn Magearna zu jemanden eine starke Bindung aufgebaut hat, kann es anhand eines Lichtstrahls aus seinem Arm diese Person ausfindig machen.<br>Wird es trotzdem in einen Kampf verwickelt, klappt es seinen Arm wie eine aufblühende Blüte aus und verschießt schlagkräftige Energiestrahlen wie Lichtkanone oder Aurasphäre. Dies schlägt sich auch in seiner Spezialattacke Kanonenbouquet nieder.',forms: [{ name: 'Magearna', image:'/PokemonBilder/Magearna/Magearna_Bild.png' },{ name: 'Originalfarbe', image:'/PokemonBilder/Magearna/Magearna_OG_Bild.png' }] },
    { name: 'Marshadow', image: '/PokemonBilder/Marshadow_Bild.png', audio: '/PokemonAudio/Marshadow_Audio.mp3', style: 'Marshadow', type: 'Kampf/Geist', pokedex: '802', color: 'grau', category: 'Schattenlauer-Pokémon', size: '0,7m', weight: '22,2 Kg',info: 'Marshadow ist ein Mysteriöses Pokémon mit den Typen Kampf und Geist und existiert seit der siebten Spielgeneration.<br><br>Spezies:<br>Marshadow ist ein eher kleines, leichtes Pokémon mit einem annähernd humanoiden, kindlichen Körperbau. Es verfügt über einen relativ großen Kopf mit auffälligen, rotgelb leuchtenden Augen, einem gedrungenen Körper und kurzen, aber kräftigen Armen und Beinen. Es ist durchgängig dunkelgrau bis schwarz gefärbt. Sein Kopf wird von auffälligen Verwirbelungen verziert, auch um seinen Hals liegt eine wolkenhafte Struktur wie ein Schal. An den Füßen zieht es dunkle Zierstreifen hinter sich her.<br>Setzt Marshadow Attacken ein, nehmen die schalhafte Struktur und die obere Hälfte seines Kopfes eine blaugrüne Färbung ein, zudem züngeln geisterhafte Flammen aus seinem Kopf, seinen Handgelenken und seinen Augen hervor. Nur in dieser Form lässt sich der Unterschied der normalen zur schillernden Form feststellen, welche lilafarben aufleuchtet.<br>Marshadow ist ein geheimnisumwobenes Pokémon, das über die Fähigkeit verfügt, mit Schatten zu verschmelzen. Diese außergewöhnliche Eigenschaft erlaubt es dem Dunkelwesen-Pokémon, in den Schatten eines Gegners einzutauchen und so dessen Bewegungen und Kräfte nicht nur zu imitieren, sondern auch zu rauben. Auch spürt es die Gefühle desjenigen, dessen Schatten es bewohnt. Da es so für das menschliche Auge unsichtbar ist, wurde die Existenz des Dunkelwesen-Pokémon lange angezweifelt.<br><br>Seine Eigenschaften schlagen sich in seiner Spezialattacke Diebesschatten nieder, mit welcher es Schaden anrichtet und dem Gegner seine erhöhten Statuswerte raubt. Mithilfe des besonderen Z-Kristalls Marshadownium Z kann diese zur mächtigen Attacke Sternbild des Seelenraubes hinaufgestuft werden.<br><br>Marshadow gilt als schwächliches und feiges Pokémon, verfügt aber über große Körperkraft, die es in eine Vielzahl von Hieb- und Trittattacken gießt. Der Sage nach hat es diese Attacken gelernt, als es im Schatten eines Faustkampf-Meisters lebte. Neben Attacken des Geist-Typen wie Schattenstoß und Finsterfaust und trickreichen Kampf-Angriffen wie Fegekick oder Ableithieb zählen auch verschiedene Elementhiebe wie Eishieb und Feuerschlag zu seinem Repertoire. Marshadows Fähigkeit Techniker zeigt, dass es Attacken virtuos ausführt und dadurch auch Attacken geringerer Basisstärke effektiv einsetzen kann. Zudem bewegt es sich mit hoher Geschwindigkeit.' },
    { name: 'Zeraora', image: '/PokemonBilder/Zeraora_Bild.png', audio: '/PokemonAudio/Zeraora_Audio.mp3', style: 'Zeraora', type: 'Elektro', pokedex: '807', color: 'gelb', category: 'Donnerkrallen-Pokémon', size: '1,5m', weight: '44,5 Kg',info: 'Zeraora ist ein Mysteriöses Pokémon mit dem Typ Elektro und existiert seit der siebten Spielgeneration, in der es mit Pokémon Ultrasonne und Ultramond eingeführt wurde.<br><br>Spezies:<br>Zeraora ist ein mittelgroßes Pokémon mit einer menschenähnlichen Statur, es weißt jedoch viele katzenartige Merkmale auf. Sein schlanker, agiler Körper ist mit schwarzem, kurzem Fell am Bauch, an den Armen und an den Füßen sowie langem und struppigem gelbem Fell um die Brust, um die Beine und um die Hände bedeckt. Es geht aufrecht auf zwei krallenbewehrten Füßen. Seine Fäuste sind groß und kräftig und ebenfalls mit Klauen versehen. Zeraoras Kopf weist große, katzenartige Ohren und einen gelben Fellkranz auf. Dort finden sich auch die blitzförmigen Schnurrhaare und eine große, hellblaue Ziersträhne. In seiner schillernden Form sind die üblicherweise schwarzen Stellen türkisfarben und das sonst gelbe Fell ist weiß gefärbt. Die Schnurrhaare und die Ziersträhne sind intensiv gelb gefärbt.<br>Zeraora verfügt aber über starke Angriffskräfte und legt vor allem körperliche Stärke an den Tag. Zudem bewegt es sich mit unglaublicher Geschwindigkeit fort. Zeraora ist nicht dazu in der Lage, Elektrizität selbst zu produzieren, jedoch kann es Strom aufnehmen und speichern und schließlich freisetzen. Im Kampf stürzt sich Zeraora mit seinen großen, krallenbewehrten Pranken auf Feinde, um sie zu zerreißen, und nutzt dies für Attacken wie Schlitzer oder Kratzfurie aus. Zwar kann es keine eigene Elektrizität erzeugen, jedoch aufgenommenen Strom speichern und absetzen. Dementsprechend machen sich viele Elektro-Attacken wie Donnerschlag, Stromstoß oder seine Spezialattacke Plasmafäuste die Kraft der elektrisierten Klauen zunutze. Plasmafäuste zeigt die Eigenart, Attacken des Gegners in Elektro-Attacken zu verwandeln. Selbst, wenn Zeraora den Gegner verfehlt, richten die Funken Schaden an.<br>Sein Fell sträubt sich, wenn es viel Energie verbraucht. Mithilfe des Magnetfeldes, welches es durch Stromstöße aus seinen Hand- und Fußballen generiert, kann es schweben und sich durch die Luft mit immenser Geschwindigkeit fortbewegen. Dies bringt ihm den Ruf ein, Eigenschaften eines Blitzeinschlages zu haben. Die hohe Geschwindigkeit des Blitzsturm-Pokémon findet sich bei Attacken wie Ruckzuckhieb oder Voltwechsel wieder.' },
    { name: 'Melmetal', image: '/PokemonBilder/Melmetal/Melmetal_Bild.png', audio: '/PokemonAudio/Melmetal_Audio.mp3', style: 'Melmetal', type: 'Stahl', pokedex: '809', color: 'grau', category: 'Bolzenmutter-Pokémon', size: '2,5m', weight: '800,0 Kg',info: 'Melmetal ist ein Mysteriöses Pokémon mit dem Typ Stahl und existiert seit der siebten Spielgeneration, in der es mit Pokémon GO eingeführt wurde. Es ist die Weiterentwicklung von Meltan. Melmetal ist in der Lage, eine Gigadynamaximierung zu Gigadynamax-Melmetal durchzuführen.<br><br>Spezies:<br>Melmetal:<br>Melmetals Körper hat eine golemartige⁠  Gestalt. Sein massiver Körper teilt sich in drei Strukturelemente ein: Zum einen sein Kopf, der aus einer sechseckigen Schraubenmutter besteht, in deren rundem Loch sich eine freischwebende, schwarze Kugel befindet, die Melmetal als Auge dient. Diese kann sich je nach Emotion verformen. Diese Kopfmutter ist in einer zweiten, querliegenden Mutter, die ihm als eine Art Brustkorb dient, etwa bis zur Hälte eingesunken. Beide Schraubenmuttern sind goldgelb gefärbt. Das zweite, signifikante Strukturelement seines Aussehens ist Melmetals silber-grauer Unterkörper, der aus flüssigem Metall besteht und äußerst robust ist. Als drittes, prägendes Strukturelement schwimmen dunkelgraue Muttern unterschiedlicher Größe in seinem flüssigen Metallkörper. Diese nehmen die Funktionen der Schulterblätter, Füße und Hände ein. Genau im Zentrum seines Torsos befindet sich eine Mutter, deren Inneres ein Loch in seinem Körper erzeugt, sodass man durch es hindurchsehen kann. An Melmetals Rückseite ragt ein roter Schweif hervor, der einem Kabel ähnelt.<br>In seiner schillernden Form sind die normalerweise gelben Schraubenmuttern kupferfarben.<br>Besonders auffällig sind Melmetals lange, kräftige Arme. Es lässt seinen Brustkorb mit hoher Geschwindigkeit rotieren und holt so Schwung, um wie mit Keulen auf den Gegner einzuprügeln. Man sagt, seine Hiebe seien die stärksten aller Pokémon. Dies schlägt sich auch in seiner Spezialattacke Panzerfäuste nieder. Auch Attacken wie Wuchtschlag oder Megahieb machen sich diese Kraft zunutze. Melmetal erzeugt in seinem Inneren durch die beweglichen Metalle Elektrizität, die es z. B. mittels Ladungsstoß freisetzt.<br><br>Gigadynamax-Melmetal:<br>Besondere Exemplare können durch das Gigadynamax-Phänomen ihre Form ändern. Sie wachsen zu gigantischen Riesen aus flüssigem Metall heran. Dadurch wird Melmetal deutlich schlanker und wendiger, während seine keulenartigen Arme durch eingeschmolzene Schraubenmuttern verstärkt werden. Legenden, die von einäugigen Riesen berichten, werden heute häufig auf Gigadynamax-Melmetal zurückgeführt.<br>Wenn Melmetal seine Gigadynamax-Form annimmt, kann es dank seines flüssigen Körpers seine Fäuste über weitere Entfernung fliegen lassen und Schläge austeilen. Die Schraubenmuttern, die in seinen Händen schwimmen, bringt es zusammen, um eine harte Keule zu erschaffen und seine Schlagkraft zu erhöhen. Durch diese Bewegungen wird zudem Elektrizität in seinem Inneren erzeugt. Gigadynamax-Melmetal kann diese aus der Mutter in seinem Bauch als Hochspannungsstrahl abfeuern, der Gegner und gar Bergkuppen pulverisieren kann. Seine Stahl-Attacken verwandeln sich in die einzigartige Attacke Giga-Schmelze, bei welcher Gegner von einer Welle aus flüssigem Metall überrollt werden. Sie fügt nicht nur Schaden zu, sondern verbietet es gegnerischen Pokémon, dieselbe Attacke mehrfach hintereinander einzusetzen.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Melmetal ist ein sehr geheimnisvolles Pokémon, welches in uralten Zeiten für sein Talent verehrt wurde, Metall herstellen zu können. Dieses Pokémon ist vollkommen in Vergessenheit geraten. Aus diesem Grund ist auch sehr wenig über sein Sozialverhalten bekannt. Die letzten bekannten Aufzeichnungen zu diesem Mutter- Pokémon sind 3000 Jahre alt. Vor Kurzem ist es von Professor Willow und Professor Eich wiederentdeckt worden. Für die Fachwelt wurde hierzu ein umfangreicher Forschungsbericht veröffentlicht.<br>Wie aus dem Nichts tauchten Meltan an verschiedenen Orten der Welt auf. Professor Willow, der die Lebensräume der Pokémon untersucht, begegnete ihm auf seinen Feldforschungen. Dabei stellte sich heraus, dass alle gesichteten Exemplare tatsächlich verwandelte Ditto waren und dass kein echtes Meltan bisher gesichtet werden konnte. Als Willow seinem Mentor Professor Eich davon berichtet, erkennt dieser das Mysteriöse Pokémon Meltan von antiken Aufzeichnungen wieder. Eichs Nachforschungen ergeben, dass Meltans plötzliches Auftauchen mit dem Öffnen einer ominösen Wunderbox in Verbindung zu stehen scheint. In dieser Box befand sich nur noch eine rostige Metallkugel, die aus den gleichen Komponenten wie Meltan besteht. Wird die Box allerdings an einem Ort, der dem natürlichen Lebensraum von Meltan ähnelt, geöffnet, so entweichen aus ihr Meltan. Ungewöhnlich für ein Pokémon des Typs Stahl liebt Meltan das Grün der Natur und weniger sterile Orte wie zum Beispiel Labore. Dies scheint bei der Metallkugel im Inneren der Box eine Reaktion auszulösen, durch die das Mysteriöse Pokémon Meltan entsteht. Im Laufe dieser Feldforschungsarbeiten sind die Meltan bei Willow miteinander verschmolzen und haben sich zu Melmetal weiterentwickelt. Somit waren beide Forscher seit 3000 Jahren die ersten Zeugen dieser Weiterentwicklung.<br><br>Bisherige Forschungen ergaben, dass auch Melmetal eine Vorliebe für naturbelassene, grüne Lebensräume hat. Wenn es alt wird, beginnt sein Körper zu rosten und schließlich zu zerfallen. Die übriggebliebenen Fragmente erwachen als Meltan zu neuem Leben.',forms: [{ name: 'Melmetal', image:'/PokemonBilder/Melmetal/Melmetal_Bild.png' },{ name: 'Gigantamax-Melmetal', image:'/PokemonBilder/Melmetal/Gmax_Melmetal_Bild.png' }] },
    { name: 'Zarude', image: '/PokemonBilder/Zarude/Zarude_Bild.png', audio: '/PokemonAudio/Zarude_Audio.mp3', style: 'Zarude', type: 'Unlicht/Pflanze', pokedex: '893', color: 'grün', category: 'Schlingaffe-Pokémon', size: '1,8m', weight: '70,0 Kg',info: 'Zarude ist ein Mysteriöses Pokémon mit den Typen Unlicht und Pflanze und existiert seit der achten Spielgeneration, in der es mit dem Erweiterungspass Die Insel der Rüstung von Pokémon Schwert und Schild eingeführt wurde.<br><br>Spezies:<br>Zarude ist ein affenartiges Pokémon, das einem Pavian oder Mandrill ähnelt. Sein Körper ist hauptsächlich mit grobem, schwarzem Fell bedeckt, aber seine Brust und sein Bauch sind mit kürzerem grauem Fell gemustert. Die obere Schnauze, die Handflächen und die Augenringe sind pelzlos, und seine Haut ist hellgrau. Das Fell an der Seite des Kopfes ist zu beiden Seiten der Augen in flügelartige Kämme gebogen. Die Augen selbst haben rote Skleren, grüne Iriden und schwarze, geschlitzte Pupillen. Die Zähne sind spitz und die Eckzähne sind sichtbar, wenn das Maul geschlossen ist.<br>In seiner schillernden Form ist die Brust beige gefärbt.<br>Zarude kann aus seinen Handgelenken, seinem Hals und seinen Fußsohlen nach Belieben Greifranken ausfahren. Mit diesen starken und flexiblen Ranken greift es an, schwingt sich von Bäumen und sammelt Nahrung aus der Ferne. Zarudes Ranken können auch schnell Wunden heilen, wenn sie um sie gewickelt werden und Energie von Bäumen absorbieren. Im Ruhezustand ragen nur die Spitzen der Ranken an Zarudes Hals und Füßen über seine Haut hinaus. Die Ranken, die von Zarudes Handgelenken ausgehen, sind normalerweise in vier konzentrischen Ringen um seine Unterarme gewickelt. Gelegentlich reißen die Ranken ab und werden zu Nährstoffen für den Boden der Wälder, in denen Zarude leben.<br>Zarude sind gesellig und leben in Rudeln. Sie sind normalerweise unglaublich aggressiv und jagen den meisten anderen Pokémon Angst ein. Sie greifen jeden an, der nicht zu ihrem Rudel gehört. Im Kampf zeichnen sie sich durch ihre scharfen Klauen und ihren schnellen Verstand aus. Allerdings zeigen Zarude gelegentlich auch eine weichere Seite.<br>Seine einzige Fähigkeit ist das Floraschild und es besitzt auch keine versteckte Fähigkeit.<br>Zarude ist das einzige bekannte Pokémon, das die Attacke Dschungelheilung erlernen kann. Diese regeneriert die KP des Anwenders und seiner Mitstreiter um 25 % der maximalen Kraftpunkte des jeweiligen Pokémon und heilt alle Statusprobleme auf der Mitstreiterseite.<br><br>Hintergrundgeschichte und Mysterienstatus:<br>Zarude wird von offizieller Seite als Mysteriöses Pokémon aufgelistet und erfüllt auch diese Kriterien in den Punkten Statuswerte, besondere Attacken und Fähigkeiten (Dschungelheilung), Präsenz in den Medien (eigener Kinofilm) sowie Erhältlichkeit (ausschließlich als Event-Verteilung erhältlich). Dennoch gibt es bis jetzt keine Andeutung in dessen Hintergrundgeschichte, die Zarude besonders macht oder ihm eine Rolle in Mythen und Sagen einer Region zuweisen. Es sind lediglich Informationen zu seinem Sozialverhalten verfügbar und dass es ausschließlich in Gruppen lebt; es also in freier Wildbahn gar nicht so selten vorkommt. Mit dieser Besonderheit unterscheidet sich Zarude von anderen mysteriösen Pokémon deutlich, da diese durchweg eine Hintergrundgeschichte aufweisen können und auch in freier Wildbahn nicht in so einer vergleichbar großen Stückzahl vorkommen.',forms: [{ name: 'Zarude', image:'/PokemonBilder/Zarude/Zarude_Bild.png' },{ name: 'Dada-Zarude', image:'/PokemonBilder/Zarude/Zarude_Dada_Bild.png' }] },
    { name: 'Infamomo', image: '/PokemonBilder/Infamomo_Bild.png', audio: '/PokemonAudio/Infamomo_Audio.mp3', style: 'Infamomo', type: 'Gift/Geist', pokedex: '1025', color: 'grün', category: 'Beerdigung-Pokémon', size: '1,5m', weight: '38,0 Kg',info: 'Infamomo ist ein Mysteriöses Pokémon mit den Typen Gift und Geist und existiert seit der neunten Spielgeneration, in der es mit dem Erweiterungspass Teil 2: Die Indigoblaue Scheibe – genauer Der Schatz von Zone Null-Bonusepisode: Die Mo-Mo-Manie – von Pokémon Karmesin und Purpur eingeführt wurde. Es steht in der Anführer-Rolle der drei Gefährten.<br><br>Spezies:<br>Infamomo ist ein kleines, koboldartiges Pokémon mit einem rundlichen, lilafarbenen Körper. In seiner schillernden Form ist es gelb gefärbt. Es verfügt über ein weiß leuchtendes Gesicht, kurze Ärmchen und ein langes, geisterhaftes Zöpfchen. Besonders auffällig jedoch sind die zwei großen, pinken Schalenhälften, die es mit sich trägt. In diesen kann es sich fest einschließen und erinnert dann an einen Pfirsich. Es kann in dieser ruhenden Form viele Jahre regungslos ausharren. In seiner Schale reichert es zudem ein starkes Gift an.<br>Aus diesem Gift stellt Infamomo giftige Mochi her, die es an Menschen und Pokémon verfüttert. So kann es sich diese gefügig machen und sie an sich ketten sowie kontrollieren. Weiterhin verstärkt es so die Kräfte und Fähigkeiten seiner Lakaien und weckt Gier in ihnen. Diese Eigenschaft schlägt sich in seiner einzigartigen Attacke Giftkettung und in seiner ebenfalls einzigartigen Fähigkeit Giftpuppenspiel nieder. Seine Lakaien Boninu, Benesaru und Beatori tragen solche Giftketten an ihrem Körper mit sich.<br>Infamomo verströmt einen süßen, angenehmen Duft. Es ist bekannt dafür, sich wie ein Kleinkind zu verhalten und Tränen vorzutäuschen, um die Zuneigung anderer zu gewinnen.<br><br>Hintergrundgeschichte und Legendenstatus:<br>Legenden erzählen von einem alten Mann und einer alten Frau, die vor langer Zeit in einem weit entfernten Land lebten. Sie waren kinderlos, doch kümmerten sich liebevoll um ein kleines Pokémon namens Infamomo. Das Pokémon sehnte sich so sehr nach der Zuneigung des Pärchens, dass es mit dem Gift in seinem Körper sogenannte Ketten-Mochi formte und sie dem Paar servierte. Dieses verspeiste die köstlichen, aber toxischen Mochi und geriet so in den Bann von Infamomos Gift.<br>Doch gab es eine Nebenwirkung: Das Gift weckte eine große Gier in dem alten Pärchen, das Infamomo in der Folge mit immer neuen Bitten überhäufte. Um ihre Zuneigung nicht zu verlieren, nutzte dieses seine Ketten-Mochi, um sich das Pokémon Boninu als Gehilfen gefügig zu machen. Dieses verfügte über eine große Körperkraft und brachte dem alten Paar schon am nächsten Tag reiche Geschenke. Eines Tages hörte das Paar von der Region Kitakami, in der Gerüchten zufolge eine Reihe wertvoller Masken existierte. Obwohl Infamomo der ständigen Bitten allmählich leid wurde, brach es gemeinsam mit Boninu nach Kitakami auf.<br>Ein Gefährte war ihm nicht genug. In einem Wald trafen sie auf das Pokémon Benesaru. Dieses hat die Fähigkeit, in die nahe Zukunft zu blicken. So warnte es Infamomo und Boninu vor einem umstürzenden Baum und rettete so ihr Leben. Daraufhin machte es Infamomo zu seinem zweiten Lakaien. Als letztes trafen sie auf Beatori. Infamomo erkannte seine Fähigkeit, mit einem betörenden Duft andere gefügig zu machen. So wurde es zum dritten Lakaien gemacht und nun fühlte sich Infamomo sicher genug, die Reise anzutreten.<br>Nach beschwerlicher Reise erreichten sie schließlich die Kitakami-Region, in welcher zu der Zeit ein ausgelassenes Fest stattfand. In dessen Mitte erblickten sie einen Maskenbauer und sein Pokémon Ogerpon, die mit reich verzierten Masken geschmückt waren.<br>Benesaru kundete den Wohnort der beiden, eine Höhle in den Bergen, aus. Boninu und Beatori drangen daraufhin in die Höhle ein, wo sie die Masken heimlich stehlen wollten. Doch wurden sie vom Maskenbauer überrascht, der sich wütend auf die Eindringlinge stürzte. Die Pokémon bezwangen ihn und entkamen, doch konnte der Mann eine Maske erfolgreich verteidigen. Schon wollte Infamomo wieder in seine Heimat zurückkehren, als das Quartett vom wütenden Ogerpon erstürmt wurde. Selbst mit geballten Kräften konnten sie Ogerpon nicht bezwingen. Infamomo sah sein erträumtes Leben in Scherben und dachte, sein Tod sei gekommen. Mit letzter Kraft zog es sich daher in seine Schale zurück. Es rollte den Berg hinab und kam in einem Wald zum Liegen. Man erzählt sich, dass es dort immer noch ruhe.<br>Viele Jahre später taucht eine pfirsichartige Dekoration in Momos Gemischtwaren auf. Die Hauptfigur in Pokémon Karmesin und Purpur kommt in den Besitz einer Mysteriösen Pirsifbeere, welche angeblich nur alle paar Jahre an sehr sorgfältig gepflegten Bäumen wächst. Es kommt im Rahmen der Bonusepisode Die Mo-Mo-Manie zu einer Reihe von merkwürdigen Vorfällen, in denen Menschen plötzlich verrückt werden und sich der Hauptfigur in den Weg stellen. Schließlich wird bekannt, dass Infamomo und dessen giftige Mochi hinter den Vorfällen stecken. Nachdem es eingefangen wird, verfliegt der Spuk.' },
  ];

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit() {
    const pokemonName = this.route.snapshot.paramMap.get('name');
    if (pokemonName) {
      this.selectedPokemon = this.pokemons.find(p => p.name === pokemonName);
      // Setze das Standardbild für die aktuelle Form
      if (this.selectedPokemon?.forms?.length) {
        this.currentFormImage = this.selectedPokemon.forms[0].image;
      } else {
        this.currentFormImage = this.selectedPokemon?.image || null;
      }
      if (!this.selectedPokemon) {
        this.router.navigate(['/pokemoncards']);
      }
    } else {
      this.router.navigate(['/pokemoncards']);
    }
  }

  selectForm(form: any) {
    this.currentFormImage = form.image;
  }

  playSound() {
    if (this.selectedPokemon?.audio) {
      const audio = new Audio(this.selectedPokemon.audio);
      audio.play();
    }
  }

  goBack() {
    this.router.navigate(['/pokemoncards']);
  }
}
